import React, { useState, useRef } from "react";
import { FiUpload, FiCopy } from "react-icons/fi";
import { ToastContainer, toast } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";

function App() {
  const [inputText, setInputText] = useState("");
  const [outputText, setOutputText] = useState("");
  const [summaryType, setSummaryType] = useState("quick");
  const [length, setLength] = useState("medium");
  const [isBold, setIsBold] = useState(false);
  const [isItalic, setIsItalic] = useState(false);
  const [isLoading, setIsLoading] = useState(false);
  const fileInputRef = useRef(null);

  const handleFileUpload = (e) => {
    const file = e.target.files[0];
    if (file && file.type === "text/plain") {
      const reader = new FileReader();
      reader.onload = (event) => {
        setInputText(event.target.result);
      };
      reader.readAsText(file);
    } else {
      toast.error("Please upload a valid .txt file");
    }
  };

  const handleGenerate = async () => {
    if (!inputText.trim()) {
      toast.error("Please enter some text to summarize");
      return;
    }

    setIsLoading(true);
    setOutputText("");

    try {
      const response = await fetch("http://127.0.0.1:8000/summarize", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({
          text: inputText,
          summary_type: summaryType,
          length: length,
        }),
      });

      if (!response.ok) {
        throw new Error("Failed to generate summary");
      }

      const data = await response.json();
      setOutputText(data.summary || data.error || "No summary generated");
      toast.success("Summary generated successfully!");
    } catch (error) {
      console.error("Error:", error);
      toast.error(error.message || "Failed to generate summary");
    } finally {
      setIsLoading(false);
    }
  };

  const copyToClipboard = () => {
    navigator.clipboard.writeText(outputText);
    toast.info("Copied to clipboard!");
  };

  return (
    <div className="min-h-screen bg-dark-1">
      <ToastContainer
        position="top-center"
        autoClose={3000}
        theme="dark"
      />

      <header className="border-b border-gray-700 p-4">
        <h1 className="text-2xl font-bold text-center">Text Summarizer</h1>
      </header>

      <main className="container mx-auto p-4 md:p-6 max-w-6xl">
        <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
          {/* Left Panel - Input */}
          <div className="bg-dark-2 rounded-xl p-6 shadow-lg">
            <h2 className="text-xl font-semibold mb-4">Input Text</h2>
            <textarea
              value={inputText}
              onChange={(e) => setInputText(e.target.value)}
              className="w-full h-64 p-4 rounded-lg border border-gray-600 bg-dark-2 text-gray-100 focus:outline-none focus:ring-2 focus:ring-primary/50 resize-none"
              placeholder="Paste your text here or upload a file..."
            />

            <div className="mt-4 flex justify-center">
              <button
                onClick={() => fileInputRef.current.click()}
                className="flex items-center gap-2 px-4 py-2 bg-dark-3 hover:bg-dark-1 rounded-lg transition-colors"
              >
                <FiUpload /> Upload .txt File
              </button>
              <input
                type="file"
                ref={fileInputRef}
                onChange={handleFileUpload}
                accept=".txt"
                className="hidden"
              />
            </div>

            <div className="mt-6 space-y-4">
              <div className="flex justify-between items-center">
                <label className="text-sm font-medium">Summary Type</label>
                <div className="flex gap-4">
                  <label className="flex items-center gap-1">
                    <input
                      type="radio"
                      name="summaryType"
                      checked={summaryType === "quick"}
                      onChange={() => setSummaryType("quick")}
                      className="mr-1"
                    />
                    Quick Summary
                    <div className="group relative">
                      <span className="text-gray-400 cursor-help" title="Extractive summary using key sentences">ⓘ</span>
                    </div>
                  </label>
                  <label className="flex items-center gap-1">
                    <input
                      type="radio"
                      name="summaryType"
                      checked={summaryType === "smart"}
                      onChange={() => setSummaryType("smart")}
                      className="mr-1"
                    />
                    Smart Summary
                    <div className="group relative">
                      <span className="text-gray-400 cursor-help" title="Abstractive summary in natural language">ⓘ</span>
                    </div>
                  </label>
                </div>
              </div>

              <div className="pt-2">
                <div className="flex justify-between text-sm text-gray-400 mb-2">
                  <span>Short</span>
                  <span>Medium</span>
                  <span>Long</span>
                </div>
                <div className="relative pt-1">
                  <input
                    type="range"
                    min="0"
                    max="2"
                    step="1"
                    value={["short", "medium", "long"].indexOf(length)}
                    onChange={(e) => setLength(["short", "medium", "long"][e.target.value])}
                    className="w-full h-2 bg-gray-700 rounded-lg appearance-none cursor-pointer"
                  />
                </div>
              </div>
            </div>

            <button
              onClick={handleGenerate}
              disabled={isLoading || !inputText.trim()}
              className={`w-full mt-6 py-3 rounded-lg font-medium transition-colors ${
                isLoading || !inputText.trim()
                  ? "bg-gray-600 cursor-not-allowed"
                  : "bg-primary hover:bg-primary-hover"
              }`}
            >
              {isLoading ? "Generating..." : "Generate Summary"}
            </button>
          </div>

          {/* Right Panel - Output */}
          <div className="bg-dark-2 rounded-xl p-6 shadow-lg">
            <div className="flex justify-between items-center mb-4">
              <h2 className="text-xl font-semibold">Summary</h2>
              {outputText && (
                <div className="flex gap-2">
                  <button
                    onClick={() => setIsBold(!isBold)}
                    className={`p-2 rounded ${isBold ? "bg-dark-3" : "hover:bg-dark-3"}`}
                    title="Bold"
                  >
                    <span className="font-bold">B</span>
                  </button>
                  <button
                    onClick={() => setIsItalic(!isItalic)}
                    className={`p-2 rounded ${isItalic ? "bg-dark-3" : "hover:bg-dark-3"}`}
                    title="Italic"
                  >
                    <span className="italic">I</span>
                  </button>
                  <button
                    onClick={copyToClipboard}
                    className="p-2 rounded hover:bg-dark-3"
                    title="Copy to clipboard"
                  >
                    <FiCopy />
                  </button>
                </div>
              )}
            </div>

            <div className="relative">
              <div
                className={`w-full h-64 p-4 rounded-lg border border-gray-600 bg-dark-2 text-gray-100 resize-none ${
                  isBold ? "font-bold" : ""
                } ${isItalic ? "italic" : ""}`}
              >
                {isLoading ? (
                  <div className="h-full flex items-center justify-center">
                    <div className="animate-pulse flex flex-col items-center">
                      <div className="h-2 w-24 bg-gray-600 rounded mb-2"></div>
                      <div>Generating...</div>
                    </div>
                  </div>
                ) : outputText ? (
                  outputText
                ) : (
                  <div className="text-gray-500">
                    Your generated summary will appear here...
                  </div>
                )}
              </div>
            </div>
          </div>
        </div>
      </main>
    </div>
  );
}

export default App;
